import React from "react";

const ProductCard = ({ product, openViewModal, openEditModal }) => {
  return (
    <div
      className="bg-white rounded-xl shadow-lg hover:shadow-2xl transition-shadow duration-300 overflow-hidden transform hover:scale-105 relative cursor-pointer"
      onClick={() => openViewModal(product)}
    >
      {/* Product Image */}
      <div className="relative">
        <img
          src={product.imageUrl}
          alt={product.name}
          className="w-full h-48 object-cover object-center rounded-t-xl cursor-pointer"
          onClick={(e) => {
            e.stopPropagation();
            openViewModal(product);
          }}
          onError={(e) => {
            e.currentTarget.src =
              "https://placehold.co/400x400/94a3b8/ffffff?text=Image+Not+Found";
          }}
        />
        <span
          className={`absolute top-2 right-2 text-white text-xs font-semibold px-2.5 py-1 rounded-full ${
            !product.isActive
              ? "bg-gray-500"
              : product.onSale
              ? "bg-red-500"
              : "bg-purple-500"
          }`}
        >
          {!product.isActive
            ? "Inactive"
            : product.onSale
            ? "SALE"
            : "Available"}
        </span>
      </div>

      {/* Product Info */}
      <div className="p-5">
        <h3 className="text-xl font-bold text-gray-800 mb-1">
          {product.name}
        </h3>
        <p className="text-sm font-medium text-gray-500 mb-3">
          {product.category}
        </p>
        <p className="text-sm text-gray-500 mb-2 line-clamp-2">
          {product.description}
        </p>
        {/* Price directly under description */}
        <p className="text-lg font-extrabold text-purple-600 mb-3">
          â‚±{product.price.toFixed(2)}
        </p>
        <p className="text-sm font-semibold text-gray-700">
          Stock: <span className="text-purple-600">{product.stock}</span>
        </p>
      </div>

      {/* Edit Button */}
      <div className="p-5 pt-0">
        <button
          onClick={(e) => {
            e.stopPropagation();
            openEditModal(product);
          }}
          className="w-full bg-blue-500 text-white p-2 rounded-md hover:bg-blue-600 transition-colors"
        >
          Edit
        </button>
      </div>
    </div>
  );
};

export default ProductCard;
