import React, { useState, useEffect, useRef } from "react";
// Import mock data to be used until the backend is integrated
import { MOCK_PRODUCTS } from "../data/mockProducts.js";

/**
 * InventoryManagement Component
 * Displays a list of products with inventory management features.
 * This includes an edit modal for modifying and creating products.
 */
const InventoryManagement = () => {
  // State for search term and selected category
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("All");

  // State to hold the products that will be displayed after filtering.
  // Use a fallback empty array to prevent errors if MOCK_PRODUCTS is undefined.
  const [products, setProducts] = useState(MOCK_PRODUCTS || []);
  const [filteredProducts, setFilteredProducts] = useState(MOCK_PRODUCTS || []);

  // Get all unique categories from the mock data to populate the dropdown.
  // Use a fallback empty array to prevent the `map` error if MOCK_PRODUCTS is undefined.
  const categories = ["All", ...new Set((products || []).map((p) => p.category))];

  // State for the image modal
  const [selectedImage, setSelectedImage] = useState(null);

  // State for the staff login modal
  const [isLoginModalOpen, setIsLoginModalOpen] = useState(false);

  // States for the edit modal
  const [isEditModalOpen, setIsEditModalOpen] = useState(false);
  const [productToEdit, setProductToEdit] = useState(null);
  const [editProductForm, setEditProductForm] = useState({
    name: "",
    price: 0,
    sale: false,
    active: true,
  });

  // Ref to measure the header's height
  const headerRef = useRef(null);
  const [headerHeight, setHeaderHeight] = useState(0);

  // useEffect to dynamically measure the header height
  useEffect(() => {
    if (headerRef.current) {
      setHeaderHeight(headerRef.current.offsetHeight);
    }
  }, []);

  /**
   * Handles changes to the search term and category filter.
   */
  const handleFilterChange = (newSearchTerm, newCategory, productsToFilter) => {
    let newFilteredProducts = (productsToFilter || []).filter((product) => {
      const matchesSearchTerm =
        product.name.toLowerCase().includes(newSearchTerm.toLowerCase()) ||
        (product.description && product.description.toLowerCase().includes(newSearchTerm.toLowerCase()));

      const matchesCategory =
        newCategory === "All" || product.category === newCategory;

      return matchesSearchTerm && matchesCategory;
    });
    setFilteredProducts(newFilteredProducts);
  };

  useEffect(() => {
    handleFilterChange(searchTerm, selectedCategory, products);
  }, [products, searchTerm, selectedCategory]);

  const clearSearch = () => {
    setSearchTerm("");
  };

  const openImageModal = (imageUrl) => {
    setSelectedImage(imageUrl);
  };

  const closeImageModal = () => {
    setSelectedImage(null);
  };

  const openEditModal = (product = null) => {
    setProductToEdit(product);
    if (product) {
      setEditProductForm({
        name: product.name,
        price: product.price,
        sale: product.sale || false,
        active: product.active !== false, // Default to true if not set
      });
    } else {
      setEditProductForm({
        name: "",
        price: 0,
        sale: false,
        active: true,
      });
    }
    setIsEditModalOpen(true);
  };

  const closeEditModal = () => {
    setIsEditModalOpen(false);
    setProductToEdit(null);
  };

  const handleEditChange = (e) => {
    const { name, value, type, checked } = e.target;
    setEditProductForm(prev => ({
      ...prev,
      [name]: type === "checkbox" ? checked : value,
    }));
  };

  const saveProduct = () => {
    if (!editProductForm.name || !editProductForm.price) {
        // Use a custom alert message as browser alerts are not supported
        document.getElementById('edit-error-message').innerText = "Please fill in all required fields.";
        return;
    }
    document.getElementById('edit-error-message').innerText = "";
    
    if (productToEdit) {
      // Modify existing product
      const updatedProducts = products.map(p =>
        p.id === productToEdit.id ? { ...p, ...editProductForm } : p
      );
      setProducts(updatedProducts);
    } else {
      // Create new product
      const newProduct = {
        id: products.length + 1, // Simple ID for mock data
        imageUrl: `https://placehold.co/400x400/94a3b8/ffffff?text=${editProductForm.name}`,
        ...editProductForm,
      };
      setProducts([...products, newProduct]);
    }
    closeEditModal();
  };

  return (
    <div className="bg-gray-50 min-h-screen flex flex-col">
      {/* Header Section */}
      <header
        ref={headerRef}
        className="bg-white shadow-md p-4 flex justify-between items-center fixed top-0 left-0 w-full z-20"
      >
        {/* Logo and Company Name */}
        <div className="flex items-center space-x-3">
          {/* Replace with your logo */}
          <div className="w-10 h-10 bg-purple-500 rounded-full flex items-center justify-center text-white font-bold text-lg">
            L
          </div>
          <h1 className="text-xl font-bold text-gray-800">Your Company Name</h1>
        </div>

        {/* Navigation Menu Button */}
        <button
          onClick={() => setIsLoginModalOpen(true)}
          className="text-gray-600 hover:text-purple-600 focus:outline-none focus:ring-2 focus:ring-purple-500 rounded-md p-2"
        >
          <svg
            className="w-6 h-6"
            fill="none"
            stroke="currentColor"
            viewBox="0 0 24 24"
            xmlns="http://www.w3.org/2000/svg"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth="2"
              d="M4 6h16M4 12h16m-7 6h7"
            ></path>
          </svg>
        </button>
      </header>

      {/* Main Content */}
      <div className="flex-grow container mx-auto p-4 sm:p-6 lg:p-8" style={{ paddingTop: `${headerHeight + 20}px` }}>
        <h2 className="text-4xl font-extrabold text-gray-900 mb-4 text-center">
          Inventory Management
        </h2>
        <p className="text-xl text-gray-600 mb-8 text-center">
          Manage your products, prices, and stock status.
        </p>

        {/* Create New Product Button */}
        <div className="flex justify-center mb-8">
            <button
                onClick={() => openEditModal()}
                className="bg-green-600 text-white p-3 rounded-lg shadow-md hover:bg-green-700 transition-colors transform hover:scale-105"
            >
                Create New Product
            </button>
        </div>

        {/* Search and filter controls */}
        <div className="flex flex-col sm:flex-row justify-center items-center gap-4 mb-8">
          <div className="relative w-full sm:w-1/2">
            <input
              type="text"
              placeholder="Search products..."
              className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-500 transition-all pr-10"
              value={searchTerm}
              onChange={(e) => {
                setSearchTerm(e.target.value);
              }}
            />
            {searchTerm && (
              <button
                onClick={clearSearch}
                className="absolute right-3 top-1/2 transform -translate-y-1/2 text-gray-400 hover:text-gray-600 focus:outline-none"
              >
                <svg
                  className="w-5 h-5"
                  fill="none"
                  stroke="currentColor"
                  viewBox="0 0 24 24"
                  xmlns="http://www.w3.org/2000/svg"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth="2"
                    d="M6 18L18 6M6 6l12 12"
                  ></path>
                </svg>
              </button>
            )}
          </div>
          <select
            className="w-full sm:w-1/4 p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-500 transition-all"
            value={selectedCategory}
            onChange={(e) => {
              setSelectedCategory(e.target.value);
            }}
          >
            {categories.map((category) => (
              <option key={category} value={category}>
                {category}
              </option>
            ))}
          </select>
        </div>

        {/* Conditional rendering to prevent the 'map' error */}
        {filteredProducts && filteredProducts.length > 0 ? (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
            {filteredProducts.map((product) => (
              <div
                key={product.id}
                className="bg-white rounded-xl shadow-lg hover:shadow-2xl transition-shadow duration-300 overflow-hidden transform hover:scale-105 relative"
              >
                <div className="relative">
                  <img
                    src={product.imageUrl}
                    alt={product.name}
                    className="w-full h-48 object-cover object-center rounded-t-xl cursor-pointer"
                    onClick={() => openImageModal(product.imageUrl)}
                    onError={(e) =>
                      (e.target.src = `https://placehold.co/400x400/94a3b8/ffffff?text=Image+Not+Found`)
                    }
                  />
                  <span className={`absolute top-2 right-2 text-white text-xs font-semibold px-2.5 py-1 rounded-full ${
                    product.active ? (product.sale ? "bg-red-500" : "bg-purple-500") : "bg-gray-500"
                  }`}>
                    {product.active ? (product.sale ? "SALE" : "â‚±" + product.price.toFixed(2)) : "Inactive"}
                  </span>
                </div>
                <div className="p-5">
                  <h3 className="text-xl font-bold text-gray-800 mb-1">
                    {product.name}
                  </h3>
                  <p className="text-sm font-medium text-gray-500 mb-3">
                    {product.category}
                  </p>
                  <p className="text-sm text-gray-500 line-clamp-2">
                    {product.description}
                  </p>
                </div>
                <div className="p-5 pt-0">
                    <button
                        onClick={() => openEditModal(product)}
                        className="w-full bg-blue-500 text-white p-2 rounded-md hover:bg-blue-600 transition-colors"
                    >
                        Edit
                    </button>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div className="text-center text-xl text-gray-500 mt-10">
            No products found. Try a different search! ðŸ”Ž
          </div>
        )}
      </div>

      {/* Image Modal */}
      {selectedImage && (
        <div
          className="fixed inset-0 bg-gray-900 bg-opacity-75 flex justify-center items-center p-4 z-50"
          onClick={closeImageModal}
        >
          <div
            className="relative p-6 bg-white rounded-xl shadow-2xl max-w-lg w-full transform scale-100 transition-transform duration-300"
            onClick={(e) => e.stopPropagation()}
          >
            <button
              onClick={closeImageModal}
              className="absolute top-3 right-3 text-white bg-red-600 rounded-full w-8 h-8 flex justify-center items-center hover:bg-red-700 transition-colors"
            >
              <span className="text-xl leading-none">&times;</span>
            </button>
            <img
              src={selectedImage}
              alt="Enlarged"
              className="w-full rounded-lg"
            />
          </div>
        </div>
      )}

      {/* Staff Login Modal */}
      {isLoginModalOpen && (
        <div className="fixed inset-0 z-50 flex items-center justify-center bg-gray-900 bg-opacity-75 transition-opacity duration-300 opacity-100">
          <div
            className="bg-white p-8 rounded-lg shadow-xl w-96 transform transition-transform duration-300 scale-100"
            onClick={(e) => e.stopPropagation()}
          >
            <h3 className="text-2xl font-bold mb-4">Staff Login</h3>
            <p className="mb-6 text-gray-600">
              This is a placeholder for your staff login page. You will implement a secure login form and redirection to the staff dashboard here.
            </p>
            <button
              onClick={() => setIsLoginModalOpen(false)}
              className="w-full bg-purple-600 text-white p-3 rounded-lg hover:bg-purple-700 transition-colors"
            >
              Close
            </button>
          </div>
        </div>
      )}

      {/* Edit Product Modal */}
      {isEditModalOpen && (
        <div className="fixed inset-0 z-50 flex items-center justify-center bg-gray-900 bg-opacity-75 transition-opacity duration-300 opacity-100">
            <div
                className="bg-white p-8 rounded-lg shadow-xl w-96 transform transition-transform duration-300 scale-100"
                onClick={(e) => e.stopPropagation()}
            >
                <h3 className="text-2xl font-bold mb-4">
                    {productToEdit ? "Modify Product" : "Create New Product"}
                </h3>
                <div id="edit-error-message" className="text-red-500 mb-4"></div>
                <div className="space-y-4">
                    <div>
                        <label className="block text-sm font-medium text-gray-700">Product Name</label>
                        <input
                            type="text"
                            name="name"
                            value={editProductForm.name}
                            onChange={handleEditChange}
                            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-purple-500 focus:ring-purple-500"
                            readOnly={!!productToEdit}
                        />
                        {productToEdit && (
                          <p className="text-xs text-gray-500 mt-1">
                            *Name cannot be changed for an existing product.
                          </p>
                        )}
                    </div>
                    <div>
                        <label className="block text-sm font-medium text-gray-700">Price</label>
                        <input
                            type="number"
                            name="price"
                            value={editProductForm.price}
                            onChange={handleEditChange}
                            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-purple-500 focus:ring-purple-500"
                            step="0.01"
                        />
                    </div>
                    <div className="flex items-center">
                        <input
                            type="checkbox"
                            id="sale"
                            name="sale"
                            checked={editProductForm.sale}
                            onChange={handleEditChange}
                            className="h-4 w-4 rounded border-gray-300 text-purple-600 focus:ring-purple-500"
                        />
                        <label htmlFor="sale" className="ml-2 block text-sm text-gray-900">
                            On Sale
                        </label>
                    </div>
                    <div className="flex items-center">
                        <input
                            type="checkbox"
                            id="active"
                            name="active"
                            checked={editProductForm.active}
                            onChange={handleEditChange}
                            className="h-4 w-4 rounded border-gray-300 text-purple-600 focus:ring-purple-500"
                        />
                        <label htmlFor="active" className="ml-2 block text-sm text-gray-900">
                            Active
                        </label>
                    </div>
                </div>
                <div className="flex justify-end space-x-3 mt-6">
                    <button
                        onClick={closeEditModal}
                        className="px-4 py-2 bg-gray-200 text-gray-700 rounded-lg shadow-sm hover:bg-gray-300 transition-colors"
                    >
                        Cancel
                    </button>
                    <button
                        onClick={saveProduct}
                        className="px-4 py-2 bg-purple-600 text-white rounded-lg shadow-sm hover:bg-purple-700 transition-colors"
                    >
                        Save
                    </button>
                </div>
            </div>
        </div>
      )}
      
      {/* Footer */}
      <footer className="bg-white shadow-inner mt-8 py-8 text-center text-gray-500 text-sm flex flex-col items-center">
        <div className="flex space-x-6 mb-4">
          <a href="https://www.facebook.com" target="_blank" rel="noopener noreferrer" aria-label="Facebook">
            <svg className="w-8 h-8 text-gray-400 hover:text-blue-600 transition-colors" fill="currentColor" viewBox="0 0 24 24">
              <path d="M9 8h-3v4h3v12h5v-12h3.642l.358-4h-4v-1.908c0-.853.183-1.428 1.429-1.428h2.071v-4h-3.29c-3.415 0-4.71 2.37-4.71 4.296v2.104z"></path>
            </svg>
          </a>
