import React, { useState, useEffect, useRef } from "react";

// Mock data (replace with AWS data later)
export const MOCK_PRODUCTS = [
  {
    id: "prod_1",
    name: "Hydrating Face Serum",
    category: "Skincare",
    description: "A deeply hydrating serum with hyaluronic acid and vitamin C.",
    price: 1999.0,
    imageUrl: "https://placehold.co/400x400?text=Serum",
    onSale: true,
  },
  {
    id: "prod_2",
    name: "Revitalizing Night Cream",
    category: "Skincare",
    description: "Nourishes and repairs your skin while you sleep.",
    price: 2499.0,
    imageUrl: "https://placehold.co/400x400?text=Cream",
    onSale: false,
  },
  {
    id: "prod_3",
    name: "Organic Green Tea Cleanser",
    category: "Cleansers",
    description: "Gentle facial cleanser with organic green tea extracts.",
    price: 1299.0,
    imageUrl: "https://placehold.co/400x400?text=Cleanser",
    onSale: false,
  },
  {
    id: "prod_4",
    name: "Vitamin C Brightening Toner",
    category: "Toners",
    description: "Brightens and evens out skin tone with Vitamin C.",
    price: 1599.0,
    imageUrl: "https://placehold.co/400x400?text=Toner",
    onSale: true,
  },
  {
    id: "prod_5",
    name: "Aloe Vera Moisturizer",
    category: "Moisturizers",
    description: "Lightweight daily moisturizer with aloe vera extract.",
    price: 1799.0,
    imageUrl: "https://placehold.co/400x400?text=Moisturizer",
    onSale: false,
  },
];

// Format price helper
const formatPrice = (price) =>
  new Intl.NumberFormat("en-PH", {
    style: "currency",
    currency: "PHP",
  }).format(price);

export default function ProductCatalogue() {
  // Core State
  const [products, setProducts] = useState(MOCK_PRODUCTS);
  const [activeProducts, setActiveProducts] = useState(MOCK_PRODUCTS);
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("All");

  // Modal State
  const [isProductModalOpen, setIsProductModalOpen] = useState(false);
  const [selectedProduct, setSelectedProduct] = useState(null);

  // Pagination State
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 6;

  // Header Ref
  const headerRef = useRef(null);
  const [headerHeight, setHeaderHeight] = useState(0);

  // Fetch products (replace with AWS later)
  useEffect(() => {
    setProducts(MOCK_PRODUCTS);
    setActiveProducts(MOCK_PRODUCTS);
  }, []);

  // Track header height
  useEffect(() => {
    if (headerRef.current) {
      setHeaderHeight(headerRef.current.offsetHeight);
    }
  }, []);

  // Filter products
  const handleFilterChange = (category, search = searchTerm) => {
    let filtered = products.filter(
      (product) =>
        product.name.toLowerCase().includes(search.toLowerCase()) ||
        product.description.toLowerCase().includes(search.toLowerCase())
    );

    if (category !== "All") {
      if (category === "Sale") {
        filtered = filtered.filter((product) => product.onSale);
      } else {
        filtered = filtered.filter((product) => product.category === category);
      }
    }

    setActiveProducts(filtered);
    setSelectedCategory(category);
    setSearchTerm(search);
    setCurrentPage(1);
  };

  // Modal controls
  const openProductModal = (product) => {
    setSelectedProduct(product);
    setIsProductModalOpen(true);
  };

  const closeProductModal = () => {
    setSelectedProduct(null);
    setIsProductModalOpen(false);
  };

  // Pagination logic
  const totalPages = Math.ceil(activeProducts.length / itemsPerPage);
  const paginatedProducts = activeProducts.slice(
    (currentPage - 1) * itemsPerPage,
    currentPage * itemsPerPage
  );

  return (
    <div className="min-h-screen bg-gray-50">
      {/* HEADER */}
      <header
        ref={headerRef}
        className="fixed top-0 left-0 w-full bg-white shadow-md z-50"
      >
        <div className="max-w-7xl mx-auto px-4 py-4 flex justify-between items-center">
          <h1 className="text-2xl font-bold text-gray-800">
            Beauty Essentials
          </h1>
          <nav className="flex gap-4">
            <button
              onClick={() => handleFilterChange("All")}
              className={`px-3 py-1 rounded ${
                selectedCategory === "All"
                  ? "bg-gray-800 text-white"
                  : "bg-gray-100 text-gray-800"
              }`}
            >
              All
            </button>
            <button
              onClick={() => handleFilterChange("Skincare")}
              className={`px-3 py-1 rounded ${
                selectedCategory === "Skincare"
                  ? "bg-gray-800 text-white"
                  : "bg-gray-100 text-gray-800"
              }`}
            >
              Skincare
            </button>
            <button
              onClick={() => handleFilterChange("Cleansers")}
              className={`px-3 py-1 rounded ${
                selectedCategory === "Cleansers"
                  ? "bg-gray-800 text-white"
                  : "bg-gray-100 text-gray-800"
              }`}
            >
              Cleansers
            </button>
            <button
              onClick={() => handleFilterChange("Toners")}
              className={`px-3 py-1 rounded ${
                selectedCategory === "Toners"
                  ? "bg-gray-800 text-white"
                  : "bg-gray-100 text-gray-800"
              }`}
            >
              Toners
            </button>
            <button
              onClick={() => handleFilterChange("Moisturizers")}
              className={`px-3 py-1 rounded ${
                selectedCategory === "Moisturizers"
                  ? "bg-gray-800 text-white"
                  : "bg-gray-100 text-gray-800"
              }`}
            >
              Moisturizers
            </button>
            <button
              onClick={() => handleFilterChange("Sale")}
              className={`px-3 py-1 rounded ${
                selectedCategory === "Sale"
                  ? "bg-red-600 text-white"
                  : "bg-red-100 text-red-600"
              }`}
            >
              Sale
            </button>
          </nav>
        </div>
      </header>

      {/* CONTENT */}
      <main
        className="max-w-7xl mx-auto px-4"
        style={{ paddingTop: `${headerHeight + 20}px` }}
      >
        {/* Search */}
        <div className="mb-6">
          <input
            type="text"
            placeholder="Search products..."
            value={searchTerm}
            onChange={(e) => handleFilterChange(selectedCategory, e.target.value)}
            className="w-full p-3 border rounded-lg shadow-sm focus:ring focus:ring-indigo-200"
          />
        </div>

        {/* Product Grid */}
        {paginatedProducts.length > 0 ? (
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            {paginatedProducts.map((product) => (
              <div
                key={product.id}
                className="bg-white rounded-lg shadow-md overflow-hidden hover:shadow-lg transition-shadow"
              >
                <div className="relative">
                  <img
                    src={product.imageUrl || "https://placehold.co/400x400"}
                    alt={product.name}
                    className="w-full h-48 object-cover"
                  />
                  {product.onSale && (
                    <span className="absolute top-2 right-2 bg-red-600 text-white text-xs font-bold px-2 py-1 rounded">
                      SALE
                    </span>
                  )}
                </div>
                <div className="p-4">
                  <h3 className="text-lg font-semibold text-gray-800">
                    {product.name}
                  </h3>
                  <p className="text-sm text-gray-500 mb-2">
                    {product.category}
                  </p>
                  <p className="text-indigo-600 font-bold">
                    {formatPrice(product.price)}
                  </p>
                  <button
                    onClick={() => openProductModal(product)}
                    className="mt-3 w-full bg-indigo-600 text-white py-2 rounded-lg hover:bg-indigo-700 transition"
                  >
                    View Details
                  </button>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <p className="text-center text-gray-500">No products found.</p>
        )}

        {/* Pagination */}
        {totalPages > 1 && (
          <div className="mt-8 flex justify-center items-center gap-2">
            <button
              onClick={() => setCurrentPage((p) => Math.max(p - 1, 1))}
              disabled={currentPage === 1}
              className="px-3 py-1 bg-gray-100 rounded disabled:opacity-50"
            >
              Prev
            </button>
            {Array.from({ length: totalPages }, (_, i) => (
              <button
                key={i + 1}
                onClick={() => setCurrentPage(i + 1)}
                className={`px-3 py-1 rounded ${
                  currentPage === i + 1
                    ? "bg-indigo-600 text-white"
                    : "bg-gray-100 text-gray-800"
                }`}
              >
                {i + 1}
              </button>
            ))}
            <button
              onClick={() => setCurrentPage((p) => Math.min(p + 1, totalPages))}
              disabled={currentPage === totalPages}
              className="px-3 py-1 bg-gray-100 rounded disabled:opacity-50"
            >
              Next
            </button>
          </div>
        )}
      </main>

      {/* PRODUCT MODAL */}
      {isProductModalOpen && selectedProduct && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-50">
          <div className="bg-white rounded-lg max-w-md w-full p-6 relative">
            <button
              onClick={closeProductModal}
              className="absolute top-2 right-2 text-gray-500 hover:text-gray-700"
            >
              âœ•
            </button>
            <img
              src={selectedProduct.imageUrl || "https://placehold.co/400x400"}
              alt={selectedProduct.name}
              className="w-full h-56 object-cover rounded"
            />
            <h2 className="text-2xl font-bold mt-4">{selectedProduct.name}</h2>
            <p className="text-gray-500">{selectedProduct.category}</p>
            <p className="mt-2">{selectedProduct.description}</p>
            <p className="text-indigo-600 font-bold text-xl mt-4">
              {formatPrice(selectedProduct.price)}
            </p>
          </div>
        </div>
      )}
    </div>
  );
}
