import React, { useState, useEffect } from "react";

// Mock data to simulate products from a database.
// This will be replaced with data from your AWS backend later.
const MOCK_PRODUCTS = [
    {
        id: "prod_1",
        name: "Hydrating Face Serum",
        category: "Skincare",
        description: "A deeply hydrating serum with hyaluronic acid and vitamin C.",
        price: 1999.0,
        imageUrl: "https://placehold.co/400x400/805ad5/ffffff?text=Serum",
    },
    {
        id: "prod_2",
        name: "Gentle Cleansing Balm",
        category: "Cleanser",
        description:
            "A soothing balm that melts away makeup and impurities without stripping the skin.",
        price: 1250.0,
        imageUrl: "https://placehold.co/400x400/4c51bf/ffffff?text=Balm",
    },
    {
        id: "prod_3",
        name: "Daily Sunscreen SPF 50",
        category: "Sun Protection",
        description: "Lightweight, non-greasy broad-spectrum SPF 50 protection.",
        price: 1600.0,
        imageUrl: "https://placehold.co/400x400/38a169/ffffff?text=Sunscreen",
    },
    {
        id: "prod_4",
        name: "Retinol Night Cream",
        category: "Skincare",
        description:
            "An anti-aging night cream to reduce fine lines and improve skin texture.",
        price: 2750.0,
        imageUrl: "https://placehold.co/400x400/d53f8c/ffffff?text=Retinol",
    },
    {
        id: "prod_5",
        name: "Exfoliating Scrub",
        category: "Skincare",
        description:
            "A gentle scrub to remove dead skin cells and reveal a brighter complexion.",
        price: 950.0,
        imageUrl: "https://placehold.co/400x400/a0aec0/ffffff?text=Scrub",
    },
    {
        id: "prod_6",
        name: "Face Mask (Hydrating)",
        category: "Skincare",
        description: "A deep hydrating mask for dry and dull skin.",
        price: 800.0,
        imageUrl: "https://placehold.co/400x400/a0aec0/ffffff?text=Mask",
    },
    {
        id: "prod_7",
        name: "Lip Balm with SPF",
        category: "Sun Protection",
        description: "Moisturizing lip balm with sun protection.",
        price: 450.0,
        imageUrl: "https://placehold.co/400x400/cbd5e0/ffffff?text=Lip+Balm",
    },
    {
        id: "prod_8",
        name: "Eye Cream",
        category: "Skincare",
        description: "Reduces puffiness and dark circles under the eyes.",
        price: 1500.0,
        imageUrl: "https://placehold.co/400x400/718096/ffffff?text=Eye+Cream",
    },
];

const ProductCatalogue = () => {
    const [products, setProducts] = useState(MOCK_PRODUCTS);
    const [selectedImage, setSelectedImage] = useState(null);
    const [searchTerm, setSearchTerm] = useState("");
    const [selectedCategory, setSelectedCategory] = useState("All");
    const [filteredProducts, setFilteredProducts] = useState(MOCK_PRODUCTS);

    // Get all unique categories from the mock data
    const categories = ["All", ...new Set(MOCK_PRODUCTS.map((p) => p.category))];

    useEffect(() => {
        let newFilteredProducts = MOCK_PRODUCTS.filter((product) => {
            // Filter by search term
            const matchesSearchTerm =
                product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                product.description.toLowerCase().includes(searchTerm.toLowerCase());

            // Filter by category
            const matchesCategory =
                selectedCategory === "All" || product.category === selectedCategory;

            return matchesSearchTerm && matchesCategory;
        });
        setFilteredProducts(newFilteredProducts);
    }, [searchTerm, selectedCategory]);

    const openModal = (imageUrl) => {
        setSelectedImage(imageUrl);
    };

    const closeModal = () => {
        setSelectedImage(null);
    };

    return (
        <div className="container mx-auto p-4 sm:p-6 lg:p-8">
            <h2 className="text-4xl font-extrabold text-gray-900 mb-4 text-center">
                Product Catalogue
            </h2>
            <p className="text-xl text-gray-600 mb-8 text-center">
                Browse our collection of products.
            </p>

            {/* Search and filter controls */}
            <div className="flex flex-col sm:flex-row justify-center items-center gap-4 mb-8">
                <input
                    type="text"
                    placeholder="Search products..."
                    className="w-full sm:w-1/2 p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-500 transition-all"
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                />
                <select
                    className="w-full sm:w-1/4 p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-500 transition-all"
                    value={selectedCategory}
                    onChange={(e) => setSelectedCategory(e.target.value)}
                >
                    {categories.map((category) => (
                        <option key={category} value={category}>
                            {category}
                        </option>
                    ))}
                </select>
            </div>

            {/* Grid for displaying product cards, responsive layout */}
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
                {filteredProducts.map((product) => (
                    <div
                        key={product.id}
                        className="bg-white rounded-xl shadow-lg hover:shadow-2xl transition-shadow duration-300 overflow-hidden transform hover:scale-105"
                    >
                        <div className="relative">
                            <img
                                src={product.imageUrl}
                                alt={product.name}
                                className="w-full h-48 object-cover object-center rounded-t-xl cursor-pointer"
                                onClick={() => openModal(product.imageUrl)}
                                onError={(e) =>
                                    (e.target.src = `https://placehold.co/400x400/94a3b8/ffffff?text=Image+Not+Found`)
                                }
                            />
                            <span className="absolute top-2 right-2 bg-purple-500 text-white text-xs font-semibold px-2.5 py-1 rounded-full">
                                â‚±{product.price.toFixed(2)}
                            </span>
                        </div>

                        <div className="p-5">
                            <h3 className="text-xl font-bold text-gray-800 mb-1">
                                {product.name}
                            </h3>
                            <p className="text-sm font-medium text-gray-500 mb-3">
                                {product.category}
                            </p>
                            <p className="text-sm text-gray-500 line-clamp-2">
                                {product.description}
                            </p>
                        </div>
                    </div>
                ))}
            </div>

            {/* Image Modal */}
            {selectedImage && (
                <div
                    className="fixed inset-0 bg-gray-900 bg-opacity-75 flex justify-center items-center p-4 z-50"
                    onClick={closeModal}
                >
                    <div
                        className="relative p-6 bg-white rounded-xl shadow-2xl max-w-lg w-full transform scale-100 transition-transform duration-300"
                        onClick={(e) => e.stopPropagation()}
                    >
                        <button
                            onClick={closeModal}
                            className="absolute top-3 right-3 text-white bg-red-600 rounded-full w-8 h-8 flex justify-center items-center hover:bg-red-700 transition-colors"
                        >
                            <span className="text-xl leading-none">&times;</span>
                        </button>
                        <img
                            src={selectedImage}
                            alt="Enlarged"
                            className="w-full rounded-lg"
                        />
                    </div>
                </div>
            )}
        </div>
    );
};

export default ProductCatalogue;
