import React, { useState, useEffect, useRef, useMemo } from "react";
import { MOCK_PRODUCTS } from "../data/mockProducts.js";
import ProductCard from "../components/ProductCard.jsx";
import ProductViewModal from "../components/ProductViewModal.jsx";
import ProductEditModal from "../components/ProductEditModal.jsx";
import Pagination from "../components/Pagination.jsx";

const AdminDashboard = () => {
  // Authentication state
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [loginForm, setLoginForm] = useState({ username: '', password: '' });
  const [loginError, setLoginError] = useState('');

  // Navigation state
  const [activeTab, setActiveTab] = useState('inventory');

  // Product management state
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("All");
  const [products, setProducts] = useState(MOCK_PRODUCTS || []);
  const [filteredProducts, setFilteredProducts] = useState(MOCK_PRODUCTS || []);
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 8;
  const [paginatedProducts, setPaginatedProducts] = useState([]);
  
  // Enhanced filtering with sort options
  const [sortBy, setSortBy] = useState('name');
  const [sortOrder, setSortOrder] = useState('asc');
  
  const categories = ["All", ...new Set((products || []).map((p) => p.category))];

  // Modal states
  const [productToView, setProductToView] = useState(null);
  const [isEditModalOpen, setIsEditModalOpen] = useState(false);
  const [productToEdit, setProductToEdit] = useState(null);
  const [editProductForm, setEditProductForm] = useState({
    name: "",
    description: "",
    originalPrice: 0,
    salePrice: 0,
    onSale: false,
    isActive: true,
    stock: 0,
    category: "",
  });

  // Sales state
  const [cart, setCart] = useState([]);
  const [customerName, setCustomerName] = useState('');
  const [customerPhone, setCustomerPhone] = useState('');
  const [paymentMethod, setPaymentMethod] = useState('cash');
  const [salesHistory, setSalesHistory] = useState([]);

  // Analytics state
  const [dateRange, setDateRange] = useState('today');

  const headerRef = useRef(null);
  const [headerHeight, setHeaderHeight] = useState(0);

  useEffect(() => {
    if (headerRef.current) {
      setHeaderHeight(headerRef.current.offsetHeight);
    }
  }, []);

  // Authentication functions
  const handleLogin = (e) => {
    e.preventDefault();
    setLoginError('');
    
    // Simple authentication (replace with your actual auth logic)
    if (loginForm.username === 'admin' && loginForm.password === 'showideaadmin') {
      setIsAuthenticated(true);
      setLoginForm({ username: '', password: '' });
    } else {
      setLoginError('Invalid credentials. Please try again.');
    }
  };

  const handleLogout = () => {
    setIsAuthenticated(false);
    setActiveTab('inventory');
    setCart([]);
    setCustomerName('');
    setCustomerPhone('');
  };

  const handleLoginInputChange = (e) => {
    const { name, value } = e.target;
    setLoginForm(prev => ({ ...prev, [name]: value }));
  };

  // Enhanced product filtering with sorting
  const handleFilterChange = (newSearchTerm, newCategory, newSortBy, newSortOrder, productsToFilter) => {
    let newFilteredProducts = (productsToFilter || []).filter((product) => {
      const matchesSearchTerm =
        product.name.toLowerCase().includes(newSearchTerm.toLowerCase()) ||
        (product.description && product.description.toLowerCase().includes(newSearchTerm.toLowerCase()));
      const matchesCategory =
        newCategory === "All" || product.category === newCategory;
      return matchesSearchTerm && matchesCategory;
    });

    // Apply sorting
    newFilteredProducts.sort((a, b) => {
      let aValue = a[newSortBy];
      let bValue = b[newSortBy];
      
      if (newSortBy === 'originalPrice' || newSortBy === 'stock') {
        aValue = Number(aValue);
        bValue = Number(bValue);
      } else if (typeof aValue === 'string') {
        aValue = aValue.toLowerCase();
        bValue = bValue.toLowerCase();
      }

      if (newSortOrder === 'asc') {
        return aValue > bValue ? 1 : -1;
      } else {
        return aValue < bValue ? 1 : -1;
      }
    });

    setFilteredProducts(newFilteredProducts);
    setCurrentPage(1);
  };

  useEffect(() => {
    handleFilterChange(searchTerm, selectedCategory, sortBy, sortOrder, products);
  }, [products, searchTerm, selectedCategory, sortBy, sortOrder]);

  useEffect(() => {
    const startIndex = (currentPage - 1) * itemsPerPage;
    const endIndex = startIndex + itemsPerPage;
    setPaginatedProducts(filteredProducts.slice(startIndex, endIndex));
  }, [filteredProducts, currentPage, itemsPerPage]);

  const totalPages = Math.ceil(filteredProducts.length / itemsPerPage);

  // Product management functions
  const handlePageChange = (page) => setCurrentPage(page);
  const clearSearch = () => setSearchTerm("");
  const openViewModal = (product) => setProductToView(product);
  const closeViewModal = () => setProductToView(null);

  const openEditModal = (product = null) => {
    setProductToEdit(product);
    if (product) {
      setEditProductForm({
        name: product.name,
        description: product.description,
        originalPrice: product.originalPrice,
        salePrice: product.salePrice || 0,
        onSale: product.onSale || false,
        isActive: product.isActive !== false,
        stock: product.stock,
        category: product.category,
      });
    } else {
      setEditProductForm({
        name: "",
        description: "",
        originalPrice: 0,
        salePrice: 0,
        onSale: false,
        isActive: true,
        stock: 0,
        category: "",
      });
    }
    setIsEditModalOpen(true);
  };

  const closeEditModal = () => {
    setIsEditModalOpen(false);
    setProductToEdit(null);
  };

  const handleEditChange = (e) => {
    const { name, value, type, checked } = e.target;
    setEditProductForm(prev => ({
      ...prev,
      [name]: type === "checkbox" ? checked : value,
    }));
  };

  const handleStockChange = (amount) => {
    setEditProductForm(prev => ({
      ...prev,
      stock: Math.max(0, parseInt(prev.stock) + amount),
    }));
  };

  const saveProduct = () => {
    if (!editProductForm.name || !editProductForm.originalPrice || !editProductForm.category || !editProductForm.description) {
      alert("Please fill in all required fields.");
      return;
    }

    if (productToEdit) {
      const updatedProducts = products.map(p =>
        p.id === productToEdit.id ? { ...p, ...editProductForm } : p
      );
      setProducts(updatedProducts);
    } else {
      const newProduct = {
        id: `prod_${Date.now()}`,
        imageUrl: `https://placehold.co/400x400/94a3b8/ffffff?text=${editProductForm.name}`,
        ...editProductForm,
      };
      setProducts([...products, newProduct]);
    }
    closeEditModal();
  };

  // Enhanced sales functions
  const addToCart = (product) => {
    if (product.stock <= 0) {
      alert('Product is out of stock!');
      return;
    }

    const existingItem = cart.find(item => item.id === product.id);
    if (existingItem) {
      if (existingItem.quantity >= product.stock) {
        alert('Cannot add more items than available in stock!');
        return;
      }
      setCart(cart.map(item =>
        item.id === product.id
          ? { ...item, quantity: item.quantity + 1 }
          : item
      ));
    } else {
      setCart([...cart, { ...product, quantity: 1 }]);
    }
  };

  const removeFromCart = (productId) => {
    setCart(cart.filter(item => item.id !== productId));
  };

  const updateCartQuantity = (productId, newQuantity) => {
    if (newQuantity <= 0) {
      removeFromCart(productId);
      return;
    }

    const product = products.find(p => p.id === productId);
    if (newQuantity > product.stock) {
      alert('Cannot exceed available stock!');
      return;
    }

    setCart(cart.map(item =>
      item.id === productId
        ? { ...item, quantity: newQuantity }
        : item
    ));
  };

  const getCartTotal = () => {
    return cart.reduce((total, item) => {
      const price = item.onSale ? item.salePrice : item.originalPrice;
      return total + (price * item.quantity);
    }, 0);
  };

  const processSale = () => {
    if (cart.length === 0) {
      alert('Cart is empty!');
      return;
    }

    if (!customerName.trim()) {
      alert('Please enter customer name!');
      return;
    }

    // Update stock
    const updatedProducts = products.map(product => {
      const cartItem = cart.find(item => item.id === product.id);
      if (cartItem) {
        return { ...product, stock: product.stock - cartItem.quantity };
      }
      return product;
    });

    // Create sale record with enhanced data
    const sale = {
      id: `sale_${Date.now()}`,
      customerName,
      customerPhone,
      paymentMethod,
      items: cart,
      total: getCartTotal(),
      date: new Date().toLocaleDateString(),
      time: new Date().toLocaleTimeString(),
      timestamp: new Date().toISOString(),
    };

    setProducts(updatedProducts);
    setSalesHistory([sale, ...salesHistory]);
    setCart([]);
    setCustomerName('');
    setCustomerPhone('');
    setPaymentMethod('cash');
    alert('Sale processed successfully!');
  };

  // Analytics calculations
  const analytics = useMemo(() => {
    const now = new Date();
    const today = new Date(now.getFullYear(), now.getMonth(), now.getDate());
    const yesterday = new Date(today);
    yesterday.setDate(yesterday.getDate() - 1);
    const thisWeek = new Date(today);
    thisWeek.setDate(thisWeek.getDate() - 7);
    const thisMonth = new Date(today);
    thisMonth.setMonth(thisMonth.getMonth() - 1);

    const filterSalesByDate = (sales, fromDate) => {
      return sales.filter(sale => {
        const saleDate = new Date(sale.timestamp || sale.date);
        return saleDate >= fromDate;
      });
    };

    const todaySales = filterSalesByDate(salesHistory, today);
    const yesterdaySales = filterSalesByDate(salesHistory, yesterday).filter(sale => {
      const saleDate = new Date(sale.timestamp || sale.date);
      return saleDate < today;
    });
    const weekSales = filterSalesByDate(salesHistory, thisWeek);
    const monthSales = filterSalesByDate(salesHistory, thisMonth);

    const calculateTotal = (sales) => sales.reduce((sum, sale) => sum + sale.total, 0);
    const calculateItems = (sales) => sales.reduce((sum, sale) => sum + sale.items.reduce((itemSum, item) => itemSum + item.quantity, 0), 0);

    const lowStockProducts = products.filter(p => p.stock < 10 && p.isActive);
    const outOfStockProducts = products.filter(p => p.stock === 0 && p.isActive);

    return {
      today: { sales: todaySales.length, revenue: calculateTotal(todaySales), items: calculateItems(todaySales) },
      yesterday: { sales: yesterdaySales.length, revenue: calculateTotal(yesterdaySales), items: calculateItems(yesterdaySales) },
      week: { sales: weekSales.length, revenue: calculateTotal(weekSales), items: calculateItems(weekSales) },
      month: { sales: monthSales.length, revenue: calculateTotal(monthSales), items: calculateItems(monthSales) },
      lowStock: lowStockProducts.length,
      outOfStock: outOfStockProducts.length,
      totalProducts: products.length,
      activeProducts: products.filter(p => p.isActive).length,
    };
  }, [salesHistory, products]);

  // Login Screen
  if (!isAuthenticated) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-purple-600 via-purple-700 to-indigo-800 flex items-center justify-center">
        <div className="bg-white p-8 rounded-xl shadow-2xl w-96 max-w-sm mx-4">
          <div className="text-center mb-6">
            <div className="w-16 h-16 bg-purple-500 rounded-full flex items-center justify-center text-white font-bold text-2xl mx-auto mb-4">
              S
            </div>
            <h2 className="text-3xl font-bold text-gray-900">Show Ida</h2>
            <p className="text-gray-600 mt-2">Staff Dashboard Login</p>
          </div>

          <form onSubmit={handleLogin} className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">Username</label>
              <input
                type="text"
                name="username"
                value={loginForm.username}
                onChange={handleLoginInputChange}
                className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-500"
                placeholder="Enter your username"
                required
              />
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">Password</label>
              <input
                type="password"
                name="password"
                value={loginForm.password}
                onChange={handleLoginInputChange}
                className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-500"
                placeholder="Enter your password"
                required
              />
            </div>

            {loginError && (
              <div className="text-red-600 text-sm text-center">{loginError}</div>
            )}

            <button
              type="submit"
              className="w-full bg-purple-600 text-white py-3 rounded-lg font-medium hover:bg-purple-700 transition-colors"
            >
              Sign In
            </button>
          </form>

          <div className="text-center mt-4">
            <p className="text-xs text-gray-500">Demo: admin / showideaadmin</p>
          </div>
        </div>
      </div>
    );
  }

  // Main Dashboard
  return (
    <div className="bg-gray-50 min-h-screen flex flex-col">
      {/* Header */}
      <header
        ref={headerRef}
        className="bg-white shadow-md p-4 flex justify-between items-center fixed top-0 left-0 w-full z-20"
      >
        <div className="flex items-center space-x-3">
          <div className="w-10 h-10 bg-purple-500 rounded-full flex items-center justify-center text-white font-bold text-lg">
            S
          </div>
          <h1 className="text-xl font-bold text-gray-800">Show Ida - Staff Dashboard</h1>
        </div>
        
        {/* Navigation Tabs */}
        <div className="hidden md:flex space-x-4">
          <button
            onClick={() => setActiveTab('dashboard')}
            className={`px-4 py-2 rounded-lg font-medium transition-colors ${
              activeTab === 'dashboard'
                ? 'bg-purple-600 text-white'
                : 'text-gray-700 hover:bg-purple-50'
            }`}
          >
            Dashboard
          </button>
          <button
            onClick={() => setActiveTab('inventory')}
            className={`px-4 py-2 rounded-lg font-medium transition-colors ${
              activeTab === 'inventory'
                ? 'bg-purple-600 text-white'
                : 'text-gray-700 hover:bg-purple-50'
            }`}
          >
            Inventory
          </button>
          <button
            onClick={() => setActiveTab('sales')}
            className={`px-4 py-2 rounded-lg font-medium transition-colors ${
              activeTab === 'sales'
                ? 'bg-purple-600 text-white'
                : 'text-gray-700 hover:bg-purple-50'
            }`}
          >
            Sales
          </button>
          <button
            onClick={() => setActiveTab('history')}
            className={`px-4 py-2 rounded-lg font-medium transition-colors ${
              activeTab === 'history'
                ? 'bg-purple-600 text-white'
                : 'text-gray-700 hover:bg-purple-50'
            }`}
          >
            History
          </button>
        </div>

        <button
          onClick={handleLogout}
          className="text-gray-600 hover:text-red-600 focus:outline-none px-3 py-2 rounded-lg"
        >
          Logout
        </button>
      </header>

      <div className="flex-grow container mx-auto p-4 sm:p-6 lg:p-8" style={{ paddingTop: `${headerHeight + 20}px` }}>
        
        {/* Dashboard Overview Tab */}
        {activeTab === 'dashboard' && (
          <>
            <h2 className="text-4xl font-extrabold text-gray-900 mb-4 text-center">
              Dashboard Overview
            </h2>
            
            {/* Quick Stats */}
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
              <div className="bg-white p-6 rounded-lg shadow">
                <h3 className="text-lg font-semibold text-gray-700 mb-2">Today's Sales</h3>
                <p className="text-3xl font-bold text-green-600">{analytics.today.revenue.toFixed(2)}</p>
                <p className="text-sm text-gray-500">{analytics.today.sales} transactions</p>
              </div>
              
              <div className="bg-white p-6 rounded-lg shadow">
                <h3 className="text-lg font-semibold text-gray-700 mb-2">Items Sold Today</h3>
                <p className="text-3xl font-bold text-blue-600">{analytics.today.items}</p>
                <p className="text-sm text-gray-500">units moved</p>
              </div>
              
              <div className="bg-white p-6 rounded-lg shadow">
                <h3 className="text-lg font-semibold text-gray-700 mb-2">Low Stock Alert</h3>
                <p className="text-3xl font-bold text-orange-600">{analytics.lowStock}</p>
                <p className="text-sm text-gray-500">products below 10 units</p>
              </div>
              
              <div className="bg-white p-6 rounded-lg shadow">
                <h3 className="text-lg font-semibold text-gray-700 mb-2">Out of Stock</h3>
                <p className="text-3xl font-bold text-red-600">{analytics.outOfStock}</p>
                <p className="text-sm text-gray-500">products unavailable</p>
              </div>
            </div>

            {/* Recent Sales and Quick Actions */}
            <div className="grid lg:grid-cols-2 gap-8">
              <div className="bg-white p-6 rounded-lg shadow">
                <h3 className="text-xl font-bold text-gray-900 mb-4">Recent Sales</h3>
                {salesHistory.slice(0, 5).length === 0 ? (
                  <p className="text-gray-500">No recent sales</p>
                ) : (
                  <div className="space-y-3">
                    {salesHistory.slice(0, 5).map((sale) => (
                      <div key={sale.id} className="flex justify-between items-center py-2 border-b">
                        <div>
                          <p className="font-medium">{sale.customerName}</p>
                          <p className="text-sm text-gray-600">{sale.date} {sale.time}</p>
                        </div>
                        <p className="font-bold text-green-600">{sale.total.toFixed(2)}</p>
                      </div>
                    ))}
                  </div>
                )}
              </div>

              <div className="bg-white p-6 rounded-lg shadow">
                <h3 className="text-xl font-bold text-gray-900 mb-4">Quick Actions</h3>
                <div className="space-y-3">
                  <button
                    onClick={() => setActiveTab('sales')}
                    className="w-full bg-green-600 text-white py-3 rounded-lg font-medium hover:bg-green-700 transition-colors"
                  >
                    Start New Sale
                  </button>
                  <button
                    onClick={() => openEditModal()}
                    className="w-full bg-blue-600 text-white py-3 rounded-lg font-medium hover:bg-blue-700 transition-colors"
                  >
                    Add New Product
                  </button>
                  <button
                    onClick={() => setActiveTab('inventory')}
                    className="w-full bg-purple-600 text-white py-3 rounded-lg font-medium hover:bg-purple-700 transition-colors"
                  >
                    Manage Inventory
                  </button>
                </div>
              </div>
            </div>
          </>
        )}
        
        {/* Inventory Management Tab */}
        {activeTab === 'inventory' && (
          <>
            <h2 className="text-4xl font-extrabold text-gray-900 mb-4 text-center">
              Inventory Management
            </h2>
            <p className="text-xl text-gray-600 mb-8 text-center">
              Manage your products, prices, and stock status.
            </p>
            
            <div className="flex justify-center mb-8">
              <button
                onClick={() => openEditModal()}
                className="bg-green-600 text-white px-6 py-3 rounded-lg shadow-md hover:bg-green-700 transition-colors transform hover:scale-105"
              >
                + Create New Product
              </button>
            </div>

            {/* Enhanced Search and Filter */}
            <div className="flex flex-col lg:flex-row justify-center items-center gap-4 mb-8">
              <div className="relative w-full lg:w-1/3">
                <input
                  type="text"
                  placeholder="Search products..."
                  className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-500 transition-all pr-10"
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                />
                {searchTerm && (
                  <button
                    onClick={clearSearch}
                    className="absolute right-3 top-1/2 transform -translate-y-1/2 text-gray-400 hover:text-gray-600"
                  >
                    <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12"></path>
                    </svg>
                  </button>
                )}
              </div>
              <select
                className="w-full lg:w-1/4 p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-500"
                value={selectedCategory}
                onChange={(e) => setSelectedCategory(e.target.value)}
              >
                {categories.map((category) => (
                  <option key={category} value={category}>
                    {category}
                  </option>
                ))}
              </select>
              <select
                className="w-full lg:w-1/4 p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-500"
                value={sortBy}
                onChange={(e) => setSortBy(e.target.value)}
              >
                <option value="name">Sort by Name</option>
                <option value="originalPrice">Sort by Price</option>
                <option value="stock">Sort by Stock</option>
                <option value="category">Sort by Category</option>
              </select>
              <select
                className="w-full lg:w-1/6 p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-500"
                value={sortOrder}
                onChange={(e) => setSortOrder(e.target.value)}
              >
                <option value="asc">A-Z / Low-High</option>
                <option value="desc">Z-A / High-Low</option>
              </select>
            </div>

            {/* Products Grid */}
            {paginatedProducts && paginatedProducts.length > 0 ? (
              <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
                {paginatedProducts.map((product) => (
                  <ProductCard
                    key={product.id}
                    product={product}
                    openViewModal={openViewModal}
                    openEditModal={openEditModal}
                    isStaffView={true}
                  />
                ))}
              </div>
            ) : (
              <div className="text-center text-xl text-gray-500 mt-10">
                No products found. Try a different search! 
              </div>
            )}

            <Pagination
              currentPage={currentPage}
              totalPages={totalPages}
              handlePageChange={handlePageChange}
            />
          </>
        )}

        {/* Enhanced Sales Tab */}
        {activeTab === 'sales' && (
          <div className="grid lg:grid-cols-2 gap-8">
            {/* Products for Sale */}
            <div>
              <h2 className="text-3xl font-bold text-gray-900 mb-6">Products</h2>
              <div className="grid grid-cols-1 sm:grid-cols-2 gap-4 max-h-96 overflow-y-auto">
                {products.filter(p => p.isActive && p.stock > 0).map((product) => (
                  <div
                    key={product.id}
                    className="bg-white p-4 rounded-lg shadow hover:shadow-md transition-shadow cursor-pointer"
                    onClick={() => addToCart(product)}
                  >
                    <img
                      src={product.imageUrl}
                      alt={product.name}
                      className="w-full h-32 object-cover rounded mb-3"
                    />
                    <h4 className="font-bold text-sm">{product.name}</h4>
                    <p className="text-xs text-gray-600 mb-2">{product.category}</p>
                    <p className="text-sm font-bold text-purple-600">
                      {product.onSale ? product.salePrice.toFixed(2) : product.originalPrice.toFixed(2)}
                    </p>
                    <p className="text-xs text-gray-500">Stock: {product.stock}</p>
                  </div>
                ))}
              </div>
            </div>

            {/* Enhanced Shopping Cart */}
            <div>
              <h2 className="text-3xl font-bold text-gray-900 mb-6">Shopping Cart</h2>
              <div className="bg-white p-6 rounded-lg shadow">
                <div className="grid md:grid-cols-2 gap-4 mb-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">Customer Name *</label>
                    <input
                      type="text"
                      value={customerName}
                      onChange={(e) => setCustomerName(e.target.value)}
                      className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-500"
                      placeholder="Enter customer name"
                      required
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">Phone Number</label>
                    <input
                      type="tel"
                      value={customerPhone}
                      onChange={(e) => setCustomerPhone(e.target.value)}
                      className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-500"
                      placeholder="09XX XXX XXXX"
                    />
                  </div>
                </div>

                <div className="mb-4">
                  <label className="block text-sm font-medium text-gray-700 mb-2">Payment Method</label>
                  <select
                    value={paymentMethod}
                    onChange={(e) => setPaymentMethod(e.target.value)}
                    className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-500"
                  >
                    <option value="cash">Cash</option>
                    <option value="card">Credit/Debit Card</option>
                    <option value="gcash">GCash</option>
                    <option value="paymaya">PayMaya</option>
                  </select>
                </div>

                <div className="max-h-64 overflow-y-auto mb-4">
                  {cart.length === 0 ? (
                    <p className="text-gray-500 text-center py-8">Cart is empty</p>
                  ) : (
                    cart.map((item) => (
                      <div key={item.id} className="flex items-center justify-between py-3 border-b">
                        <div className="flex-1">
                          <h4 className="font-medium">{item.name}</h4>
                          <p className="text-sm text-gray-600">
                            {item.onSale ? item.salePrice.toFixed(2) : item.originalPrice.toFixed(2)} each
                          </p>
                        </div>
                        <div className="flex items-center space-x-2">
                          <button
                            onClick={() => updateCartQuantity(item.id, item.quantity - 1)}
                            className="w-8 h-8 bg-gray-200 rounded text-sm hover:bg-gray-300"
                          >
                            -
                          </button>
                          <span className="w-8 text-center">{item.quantity}</span>
                          <button
                            onClick={() => updateCartQuantity(item.id, item.quantity + 1)}
                            className="w-8 h-8 bg-gray-200 rounded text-sm hover:bg-gray-300"
                          >
                            +
                          </button>
                          <button
                            onClick={() => removeFromCart(item.id)}
                            className="ml-2 text-red-600 hover:text-red-800"
                          >
                            
                          </button>
                        </div>
                      </div>
                    ))
                  )}
                </div>

                <div className="border-t pt-4">
                  <div className="space-y-2 mb-4">
                    <div className="flex justify-between text-sm">
                      <span>Subtotal:</span>
                      <span>{getCartTotal().toFixed(2)}</span>
                    </div>
                    <div className="flex justify-between text-lg font-bold">
                      <span>Total:</span>
                      <span>{getCartTotal().toFixed(2)}</span>
                    </div>
                  </div>
                  <button
                    onClick={processSale}
                    disabled={cart.length === 0 || !customerName.trim()}
                    className="w-full bg-green-600 text-white py-3 rounded-lg font-medium hover:bg-green-700 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
                  >
                    Process Sale
                  </button>
                </div>
              </div>
            </div>
          </div>
        )}

        {/* Enhanced Sales History Tab */}
        {activeTab === 'history' && (
          <>
            <h2 className="text-3xl font-bold text-gray-900 mb-6">Sales History</h2>
            
            {/* Sales Analytics */}
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
              <div className="bg-white p-6 rounded-lg shadow">
                <h3 className="text-lg font-semibold text-gray-700 mb-2">This Week</h3>
                <p className="text-2xl font-bold text-green-600">{analytics.week.revenue.toFixed(2)}</p>
                <p className="text-sm text-gray-500">{analytics.week.sales} sales  {analytics.week.items} items</p>
              </div>
              
              <div className="bg-white p-6 rounded-lg shadow">
                <h3 className="text-lg font-semibold text-gray-700 mb-2">This Month</h3>
                <p className="text-2xl font-bold text-blue-600">{analytics.month.revenue.toFixed(2)}</p>
                <p className="text-sm text-gray-500">{analytics.month.sales} sales  {analytics.month.items} items</p>
              </div>
              
              <div className="bg-white p-6 rounded-lg shadow">
                <h3 className="text-lg font-semibold text-gray-700 mb-2">Total Sales</h3>
                <p className="text-2xl font-bold text-purple-600">{salesHistory.length}</p>
                <p className="text-sm text-gray-500">all time transactions</p>
              </div>
            </div>

            <div className="bg-white rounded-lg shadow overflow-hidden">
              {salesHistory.length === 0 ? (
                <p className="text-gray-500 text-center py-12">No sales recorded yet</p>
              ) : (
                <div className="overflow-x-auto">
                  <table className="w-full">
                    <thead className="bg-gray-50">
                      <tr>
                        <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Date/Time</th>
                        <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Customer</th>
                        <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Items</th>
                        <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Payment</th>
                        <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Total</th>
                      </tr>
                    </thead>
                    <tbody className="divide-y divide-gray-200">
                      {salesHistory.map((sale) => (
                        <tr key={sale.id} className="hover:bg-gray-50">
                          <td className="px-6 py-4 text-sm text-gray-900">
                            <div>
                              <p>{sale.date}</p>
                              <p className="text-xs text-gray-500">{sale.time}</p>
                            </div>
                          </td>
                          <td className="px-6 py-4 text-sm text-gray-900">
                            <div>
                              <p className="font-medium">{sale.customerName}</p>
                              {sale.customerPhone && (
                                <p className="text-xs text-gray-500">{sale.customerPhone}</p>
                              )}
                            </div>
                          </td>
                          <td className="px-6 py-4 text-sm text-gray-900">
                            <div className="max-w-xs">
                              {sale.items.map((item, index) => (
                                <div key={index} className="text-xs">
                                  {item.name}  {item.quantity}
                                </div>
                              ))}
                            </div>
                          </td>
                          <td className="px-6 py-4 text-sm text-gray-900">
                            <span className={`px-2 py-1 text-xs rounded-full ${
                              sale.paymentMethod === 'cash' ? 'bg-green-100 text-green-800' :
                              sale.paymentMethod === 'card' ? 'bg-blue-100 text-blue-800' :
                              sale.paymentMethod === 'gcash' ? 'bg-blue-100 text-blue-800' :
                              'bg-purple-100 text-purple-800'
                            }`}>
                              {sale.paymentMethod?.toUpperCase() || 'CASH'}
                            </span>
                          </td>
                          <td className="px-6 py-4 text-sm font-bold text-green-600">
                            {sale.total.toFixed(2)}
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              )}
            </div>
          </>
        )}
      </div>

      {/* Modals */}
      <ProductViewModal
        productToView={productToView}
        closeViewModal={closeViewModal}
        openEditModal={openEditModal}
        isStaffView={true}
      />

      <ProductEditModal
        isEditModalOpen={isEditModalOpen}
        closeEditModal={closeEditModal}
        productToEdit={productToEdit}
        editProductForm={editProductForm}
        handleEditChange={handleEditChange}
        handleStockChange={handleStockChange}
        saveProduct={saveProduct}
      />

      {/* Mobile Navigation */}
      <div className="md:hidden fixed bottom-0 left-0 w-full bg-white border-t shadow-lg">
        <div className="flex justify-around py-2">
          <button
            onClick={() => setActiveTab('dashboard')}
            className={`flex flex-col items-center p-2 ${
              activeTab === 'dashboard' ? 'text-purple-600' : 'text-gray-600'
            }`}
          >
            <svg className="w-6 h-6 mb-1" fill="currentColor" viewBox="0 0 20 20">
              <path d="M3 4a1 1 0 011-1h12a1 1 0 011 1v2a1 1 0 01-1 1H4a1 1 0 01-1-1V4zM3 10a1 1 0 011-1h6a1 1 0 011 1v6a1 1 0 01-1 1H4a1 1 0 01-1-1v-6zM14 9a1 1 0 00-1 1v6a1 1 0 001 1h2a1 1 0 001-1v-6a1 1 0 00-1-1h-2z" />
            </svg>
            <span className="text-xs">Dashboard</span>
          </button>
          <button
            onClick={() => setActiveTab('inventory')}
            className={`flex flex-col items-center p-2 ${
              activeTab === 'inventory' ? 'text-purple-600' : 'text-gray-600'
            }`}
          >
            <svg className="w-6 h-6 mb-1" fill="currentColor" viewBox="0 0 20 20">
              <path fillRule="evenodd" d="M10 2L3 7v11a1 1 0 001 1h12a1 1 0 001-1V7l-7-5zM10 12a2 2 0 100-4 2 2 0 000 4z" clipRule="evenodd" />
            </svg>
            <span className="text-xs">Inventory</span>
          </button>
          <button
            onClick={() => setActiveTab('sales')}
            className={`flex flex-col items-center p-2 ${
              activeTab === 'sales' ? 'text-purple-600' : 'text-gray-600'
            }`}
          >
            <svg className="w-6 h-6 mb-1" fill="currentColor" viewBox="0 0 20 20">
              <path d="M3 1a1 1 0 000 2h1.22l.305 1.222a.997.997 0 00.01.042l1.358 5.43-.893.892C3.74 11.846 4.632 14 6.414 14H15a1 1 0 000-2H6.414l1-1H14a1 1 0 00.894-.553l3-6A1 1 0 0017 3H6.28l-.31-1.243A1 1 0 005 1H3zM16 16.5a1.5 1.5 0 11-3 0 1.5 1.5 0 013 0zM6.5 18a1.5 1.5 0 100-3 1.5 1.5 0 000 3z" />
            </svg>
            <span className="text-xs">Sales</span>
          </button>
          <button
            onClick={() => setActiveTab('history')}
            className={`flex flex-col items-center p-2 ${
              activeTab === 'history' ? 'text-purple-600' : 'text-gray-600'
            }`}
          >
            <svg className="w-6 h-6 mb-1" fill="currentColor" viewBox="0 0 20 20">
              <path fillRule="evenodd" d="M3 3a1 1 0 000 2v8a2 2 0 002 2h2.586l-1.293 1.293a1 1 0 101.414 1.414L10 15.414l2.293 2.293a1 1 0 001.414-1.414L12.414 15H15a2 2 0 002-2V5a1 1 0 100-2H3zm11.707 4.707a1 1 0 00-1.414-1.414L10 9.586 8.707 8.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z" clipRule="evenodd" />
            </svg>
            <span className="text-xs">History</span>
          </button>
        </div>
      </div>

      {/* Enhanced Staff Footer with more spacing for mobile nav */}
      <footer className="bg-white border-t py-4 text-center text-gray-500 text-sm mb-16 md:mb-0">
        <div> 2025 Show Ida - Staff Dashboard. Internal use only.</div>
      </footer>
    </div>
  );
};

export default AdminDashboard;
