import React from 'react';

// ProductViewModal now accepts an additional prop: isStaffView
const ProductViewModal = ({ productToView, closeViewModal, openEditModal, isStaffView }) => {
  if (!productToView) return null;

  return (
    <div
      className="fixed inset-0 bg-gray-900 bg-opacity-75 flex justify-center items-center p-4 z-50"
      onClick={closeViewModal}
    >
      <div
        className="relative p-6 bg-white rounded-xl shadow-2xl max-w-2xl w-full transform scale-100 transition-transform duration-300"
        onClick={(e) => e.stopPropagation()}
      >
        <button
          onClick={closeViewModal}
          className="absolute top-3 right-3 text-white bg-red-600 rounded-full w-8 h-8 flex justify-center items-center hover:bg-red-700 transition-colors"
        >
          <span className="text-xl leading-none">&times;</span>
        </button>
        <div className="flex flex-col sm:flex-row gap-6">
          <div className="sm:w-1/2">
            <img
              src={productToView.imageUrl}
              alt={productToView.name}
              className="w-full rounded-lg object-cover h-64 sm:h-auto"
              onError={(e) =>
                (e.target.src = `https://placehold.co/400x400/94a3b8/ffffff?text=Image+Not+Found`)
              }
            />
          </div>
          <div className="sm:w-1/2">
            <h3 className="text-3xl font-bold text-gray-900 mb-2">
              {productToView.name}
            </h3>
            <p className="text-lg font-semibold text-purple-600 mb-4">
              â‚±{productToView.price.toFixed(2)}
              {productToView.onSale && (
                <span className="ml-3 text-sm font-normal text-red-500">On Sale!</span>
              )}
            </p>
            <p className="text-sm text-gray-500 mb-2">
              Category: {productToView.category}
            </p>
            {/* Conditionally render staff-only details */}
            {isStaffView && (
              <>
                <p className="text-sm text-gray-500 mb-4">
                  Stock: {productToView.stock}
                </p>
              </>
            )}
            <p className="text-gray-700 text-base mb-6">
              {productToView.description}
            </p>
            {/* Conditionally render staff-only details */}
            {isStaffView && (
              <p className="text-sm font-medium text-gray-500 mb-4">
                Status:{" "}
                <span className={`font-semibold ${productToView.isActive ? "text-green-600" : "text-gray-500"}`}>
                  {productToView.isActive ? "Active" : "Inactive"}
                </span>
              </p>
            )}
          </div>
        </div>
        {/* Conditionally render staff-only buttons */}
        {isStaffView && (
          <div className="flex justify-end space-x-3 mt-6">
            <button
              onClick={() => {
                closeViewModal();
                openEditModal(productToView);
              }}
              className="px-4 py-2 bg-blue-500 text-white rounded-lg shadow-sm hover:bg-blue-600 transition-colors"
            >
              Edit Product
            </button>
            <button
              onClick={closeViewModal}
              className="px-4 py-2 bg-gray-200 text-gray-700 rounded-lg shadow-sm hover:bg-gray-300 transition-colors"
            >
              Close
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default ProductViewModal;
